export default function yargs() {
  const argv = { _: [] };
  const api = {
    scriptName: () => api,
    version: () => api,
    usage: () => api,
    command: () => api,
    option: () => api,
    example: () => api,
    help: () => api,
    check: () => api,
    fail: () => api,
    group: () => api,
    wrap: () => api,
    epilogue: () => api,
    positional: () => api,
    alias: () => api,
    parse: async () => argv,
  };
  return api;
}
